tests.sql:
SELECT * 
FROM sales 
WHERE region = 'North';

SELECT * 
FROM sales 
WHERE region = 'East';

SELECT region, COUNT(*) as num_sales 
FROM sales 
GROUP BY region;

SELECT customer_type, COUNT(*) as count, SUM(amount) as total 
FROM sales 
GROUP BY customer_type;

SELECT SUM(amount) as total_sales 
FROM sales 
WHERE region = 'South';

SELECT * 
FROM sales 
WHERE product IN ('Laptop', 'Desktop');

SELECT * 
FROM sales 
WHERE EXTRACT(MONTH FROM date) IN (1, 2, 3);

SELECT SUM(amount) as total_revenue 
FROM sales 
WHERE date >= '2025-01-01';

SELECT region, customer_type, AVG(amount) as avg_sale 
FROM sales 
GROUP BY region, customer_type;

SELECT * 
FROM sales 
WHERE quantity > (SELECT AVG(quantity) FROM sales);

SELECT product, customer_type, COUNT(*) as count, SUM(amount) as total 
FROM sales 
GROUP BY product, customer_type;

SELECT *
FROM sales
WHERE amount > (SELECT AVG(amount) + 3 * STDDEV(amount) FROM sales);

SELECT region, MAX(amount) as highest_sale 
FROM sales 
GROUP BY region;

SELECT region, AVG(amount * quantity) as weighted_avg_sale 
FROM sales 
GROUP BY region;

SELECT product, COUNT(DISTINCT date) as days_of_sales 
FROM sales 
GROUP BY product;
